import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';


const Emoji = props => (
  <span
    className="emoji"
    role="img"
    aria-label={props.label ? props.label : ""}
    aria-hidden={props.label ? "false" : "true"}
  >
    {props.symbol}
  </span>
)



class App extends Component{


state = {
  list: [{text: "Buy eggs", done: false},
  {text: "Buy milk", done: true},
  ],
  input: '',
  textInput:'',
  newList: [],
  modal:false,
  modalContent:""
  }
onInputChange = (event) => {

  this.setState({input:event.target.value})
}
onTextInputChange = (event) => {
  this.setState({textInput:event.target.value})
}
onClicked = () => {
  console.log(this.state.input)
  this.setState(prevState => ({
    list: [...prevState.list, {text: this.state.input,done:false}]
  }))
  // this.state.list.push({text:this.state.input, done: false})
  console.log(this.state.list)

}
// componentDidMount = () => {
//   let newData = this.state.list
//   this.setState({newList:newData})
//   console.log(this.state.newList)
//   this.forceUpdate()
// }
deleteItem = (index) => {
  let arr = this.state.list
  arr.splice(index,1)
  this.setState({list:arr})
  console.log(this.state.list.length)

}
changeToDone = (index) => {
  console.log(this.state.list[index].done)
  let list = this.state.list
  let done = this.state.list[index]

  if(done.done === false){
    done.done = true
    console.log(done.done)
  }
  else{
    done.done = false
    console.log(done.done)
  }

  this.setState({list})
}
showModal = index => {
  console.log(index)
  this.setState({modal:true})
  this.setState({modalContent:this.state.list[index]})
  console.log(this.state.modalContent)
}

render() {
  return (
    <div className="App">
      <header className="App-header">
        <div className = "todo-list">
          <h1>To Do List</h1>         
          {(this.state.list != 0)? (this.state.list.map((todo,index)=>
              <div key={index} ><span onClick={()=> this.showModal(index)}>{todo.text} </span>
              <button onClick={() => this.changeToDone(index)}>{todo.done?<Emoji label="sheep" symbol="✅"/>:'❌'}</button>
              <button onClick={()=> this.onClicked(index)}>Delete</button></div>
              )):(<div>Nothing to do</div>)}
          <div className='input'>
              <input type='text' className='todo-item' value={this.state.input} onChange={this.onInputChange}></input><br></br><br></br>
              <textarea name="message" rows="10" cols="30" value={this.state.textInput} onChange={this.onInputChange}></textarea>
              <br></br><br></br>
              <input type='submit' className='todo-button' onClick={()=> this.onClicked()}></input>
          </div>
          {(this.state.modal)?(
           <div className='modal'></div>
          ):(<div></div>)
          }
        </div>
      </header>
    </div>
  );
}
}
export default App;
